// ** React Imports
import { useState, Fragment } from 'react'

// ** Store & Actions
import { updateStatus } from '../store'
import { useDispatch, useSelector } from 'react-redux'

// ** Reactstrap Imports
import { Row, Col, Card, Form, CardBody, Button, Badge, Modal, Input, Label, ModalBody, ModalHeader } from 'reactstrap'

// ** Third Party Components
import Swal from 'sweetalert2'
import Select from 'react-select'
import { Check, Briefcase, X } from 'react-feather'
import { useForm, Controller } from 'react-hook-form'
import withReactContent from 'sweetalert2-react-content'

// ** Custom Components
import Avatar from '@components/avatar'

// ** Utils
import { selectThemeColors } from '@utils'
import moment from 'moment'
import 'moment/locale/vi'

// ** Styles
import '@styles/react/libs/react-select/_react-select.scss'

const roleColors = {
  editor: 'light-info',
  admin: 'light-danger',
  author: 'light-warning',
  maintainer: 'light-success',
  subscriber: 'light-primary'
}

const statusColors = {
  active: 'light-success',
  pending: 'light-warning',
  inactive: 'light-secondary'
}

const statusOptions = [
  { value: '', label: 'Select Status' },
  { value: 'active', label: 'Active' },
  { value: 'inactive', label: 'Inactive' },
  { value: 'pending', label: 'Pending' }
]

const countryOptions = [
  { value: 'uk', label: 'UK' },
  { value: 'usa', label: 'USA' },
  { value: 'france', label: 'France' },
  { value: 'russia', label: 'Russia' },
  { value: 'canada', label: 'Canada' }
]

const languageOptions = [
  { value: 'english', label: 'English' },
  { value: 'spanish', label: 'Spanish' },
  { value: 'french', label: 'French' },
  { value: 'german', label: 'German' },
  { value: 'dutch', label: 'Dutch' }
]

const MySwal = withReactContent(Swal)

const UserInfoCard = ({ selectedUser, role }) => {

  const dispatch = useDispatch()

  const roleOptions = [{ value: '', label: 'Select Role' }]
  role.map((roles) => {
    roleOptions.push({
       value: roles.id, label: roles.name
    })
  })

  // ** State
  const [show, setShow] = useState(false)
  const [currentStatus, setCurrentStatus] = useState(selectedUser.status)

  // ** Hook
  const {
    reset,
    control,
    setError,
    handleSubmit,
    formState: { errors }
  } = useForm({
    defaultValues: {
      username: selectedUser.username,
      lastName: selectedUser.fullName.split(' ')[1],
      firstName: selectedUser.fullName.split(' ')[0],
      email:selectedUser.email,
      telegramId: selectedUser.telegramId,
      phone: selectedUser.phone
      // role:roleOptions[roleOptions.findIndex(i => i.value === selectedUser.roleId)],
      // status: statusOptions[statusOptions.findIndex(i => i.value === selectedUser.status)],
    }
  })

  // ** render user img
  const renderUserImg = () => {
    if (selectedUser !== null && selectedUser.avatar.length) {
      return (
        <img
          height='110'
          width='110'
          alt='user-avatar'
          src={selectedUser.avatar}
          className='img-fluid rounded mt-3 mb-2'
        />
      )
    } else {
      const stateNum = Math.floor(Math.random() * 6),
        states = ['light-success', 'light-danger', 'light-warning', 'light-info', 'light-primary', 'light-secondary'],
        color = states[stateNum]
      return (
        <Avatar
          initials
          color={color}
          className='rounded mt-3 mb-2'
          content={selectedUser.fullName}
          contentStyles={{
            borderRadius: 0,
            fontSize: 'calc(48px)',
            width: '100%',
            height: '100%'
          }}
          style={{
            height: '110px',
            width: '110px'
          }}
        />
      )
    }
  }

  const onSubmit = data => {
    console.log(Object.values(data).every(field => field.toString().length > 0))
    if (Object.values(data).every(field => field.toString().length > 0)) {
    dispatch(
      updateStatus(
        {
          id: selectedUser.id, 
          fullname: `${data.firstName  } ${  data.lastName}`,
          phone : data.phone,
          roleId: data.role,
          active: data.status,
          telegramId : data.telegramId
        }
      )
    )
      setShow(false)
    } else {
      for (const key in data) {
        if (data[key].length === 0) {
          setError(key, {
            type: 'manual'
          })
        }
      }
    }
  }

  const handleReset = () => {
    reset({
      username: selectedUser.username,
      lastName: selectedUser.fullName.split(' ')[1],
      firstName: selectedUser.fullName.split(' ')[0]
    })
  }

  const handleSuspendedClick = () => {
    return MySwal.fire({
      title: 'Are you sure?',
      text: currentStatus === 'active' ?  "You won't be able to revert user!" : "You active this user!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: currentStatus === 'active' ?  'Yes, Suspend user!' : "Yes, Active user!",
      customClass: {
        confirmButton: 'btn btn-primary',
        cancelButton: 'btn btn-outline-danger ms-1'
      },
      buttonsStyling: false
    }).then(function (result) {
      if (result.value) {
        dispatch(
          updateStatus(
            {
              id: selectedUser.id, 
              active: currentStatus === 'active' ? 'inactive' : 'active'
            }
          )
        )
        setCurrentStatus(currentStatus === 'active' ? 'inactive' : 'active')
        MySwal.fire({
          icon: 'success',
          title: currentStatus === 'active' ? 'Suspended!' : 'Activated!',
          text: currentStatus === 'active' ? 'User has been suspended.' : 'User has been activated.',
          customClass: {
            confirmButton: 'btn btn-success'
          }
        })
      } else if (result.dismiss === MySwal.DismissReason.cancel) {
        MySwal.fire({
          title: 'Cancelled',
          text: currentStatus === 'active' ? 'Cancelled Suspension :)' : 'Cancelled Activation :)',
          icon: 'error',
          customClass: {
            confirmButton: 'btn btn-success'
          }
        })
      }
    })
  }

  return (
    <Fragment>
      <Card>
        <CardBody>
          <div className='user-avatar-section'>
            <div className='d-flex align-items-center flex-column'>
              {renderUserImg()}
              <div className='d-flex flex-column align-items-center text-center'>
                <div className='user-info'>
                  <h4>{selectedUser !== null ? selectedUser.fullName : 'Eleanor Aguilar'}</h4>
                  {selectedUser !== null ? (
                    <Badge color={roleColors[selectedUser.role]} className='text-capitalize'>
                      {selectedUser.role}
                    </Badge>
                  ) : null}
                </div>
              </div>
            </div>
          </div>
          {/* <div className='d-flex justify-content-around my-2 pt-75'>
            <div className='d-flex align-items-start me-2'>
              <Badge color='light-primary' className='rounded p-75'>
                <Check className='font-medium-2' />
              </Badge>
              <div className='ms-75'>
                <h4 className='mb-0'>1.23k</h4>
                <small>Tasks Done</small>
              </div>
            </div>
            <div className='d-flex align-items-start'>
              <Badge color='light-primary' className='rounded p-75'>
                <Briefcase className='font-medium-2' />
              </Badge>
              <div className='ms-75'>
                <h4 className='mb-0'>568</h4>
                <small>Projects Done</small>
              </div>
            </div>
          </div> */}
          <h4 className='fw-bolder border-bottom pb-50 mb-1'>Details</h4>
          <div className='info-container'>
            {selectedUser !== null ? (
              <ul className='list-unstyled'>
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Username:</span>
                  <span>{selectedUser.username}</span>
                </li>
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Email:</span>
                  <span>{selectedUser.email}</span>
                </li>
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Status:</span>
                  <Badge className='text-capitalize' color={statusColors[selectedUser.status]}>
                    {selectedUser.status}
                  </Badge>
                </li>
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Role:</span>
                  <span className='text-capitalize'>{selectedUser.role}</span>
                </li>
                {/* <li className='mb-75'>
                  <span className='fw-bolder me-25'>Tax ID:</span>
                  <span>Tax-{selectedUser.contact.substr(selectedUser.contact.length - 4)}</span>
                </li> */}
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Phone:</span>
                  <span>{selectedUser.phone}</span>
                </li>
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Telegram Id:</span>
                  <span>{selectedUser.telegramId}</span>
                </li>
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Last login:</span>
                  <span>{moment(selectedUser.last_login).format('llll')}</span>
                </li>
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Created At:</span>
                  <span>{moment(selectedUser.created_at).format('llll')}</span>
                </li>
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Update At:</span>
                  <span>{moment(selectedUser.updated_at).format('llll')}</span>
                </li>
                {/* <li className='mb-75'>
                  <span className='fw-bolder me-25'>Language:</span>
                  <span>English</span>
                </li>
                <li className='mb-75'>
                  <span className='fw-bolder me-25'>Country:</span>
                  <span>England</span>
                </li> */}
              </ul>
            ) : null}
          </div>
          <div className='d-flex justify-content-center pt-2'>
            <Button color='primary' onClick={() => setShow(true)}>
              Edit
            </Button>
            <Button className='ms-1' color='danger' outline onClick={handleSuspendedClick}>
              {currentStatus !== 'active' ? 'Activate' : 'Suspended'}
            </Button>
          </div>
        </CardBody>
      </Card>
      <Modal isOpen={show} toggle={() => setShow(!show)} className='modal-dialog-centered modal-lg'>
        <ModalHeader className='bg-transparent' toggle={() => setShow(!show)}></ModalHeader>
        <ModalBody className='px-sm-5 pt-50 pb-5'>
          <div className='text-center mb-2'>
            <h1 className='mb-1'>Edit User Information</h1>
            <p>Updating user details will receive a privacy audit.</p>
          </div>
          <Form onSubmit={handleSubmit(onSubmit)}>
            <Row className='gy-1 pt-75'>
              <Col md={6} xs={12}>
                <Label className='form-label' for='firstName'>
                  First Name
                </Label>
                <Controller
                  defaultValue=''
                  control={control}
                  id='firstName'
                  name='firstName'
                  render={({ field }) => (
                    <Input {...field} id='firstName' placeholder='John' invalid={errors.firstName && true} />
                  )}
                />
              </Col>
              <Col md={6} xs={12}>
                <Label className='form-label' for='lastName'>
                  Last Name
                </Label>
                <Controller
                  defaultValue=''
                  control={control}
                  id='lastName'
                  name='lastName'
                  render={({ field }) => (
                    <Input {...field} id='lastName' placeholder='Doe' invalid={errors.lastName && true} />
                  )}
                />
              </Col>
              <Col md={6} xs={12}>
                <Label className='form-label' for='username'>
                  Username
                </Label>
                <Controller
                  defaultValue=''
                  control={control}
                  id='username'
                  name='username'
                  render={({ field }) => (
                    <Input {...field} id='username' placeholder='john.doe.007' invalid={errors.username && true} />
                  )}
                />
              </Col>
              <Col md={6} xs={12}>
                <Label className='form-label' for='email'>
                  Email
                </Label>
                <Controller
                  defaultValue=''
                  control={control}
                  id='email'
                  name='email'
                  render={({ field }) => (
                    <Input {...field} id='email' placeholder='John' invalid={errors.email && true} />
                  )}
                />
              </Col>
              {/* <Col md={6} xs={12}>
                <Label className='form-label' for='tax-id'>
                  Tax ID
                </Label>
                <Input
                  id='tax-id'
                  placeholder='Tax-1234'
                  defaultValue={selectedUser.contact.substr(selectedUser.contact.length - 4)}
                />
              </Col> */}
              <Col md={6} xs={12}>
                <Label className='form-label' for='phone'>
                  Phone
                </Label>
                <Controller
                  defaultValue=''
                  control={control}
                  id='phone'
                  name='phone'
                  render={({ field }) => (
                    <Input {...field} id='phone' placeholder='+1 609 933 4422' invalid={errors.phone && true} />
                  )}
                />
              </Col>
              <Col md={6} xs={12}>
                <Label className='form-label' for='telegramId'>
                  Telegram ID
                </Label>
                <Controller
                  defaultValue=''
                  control={control}
                  id='telegramId'
                  name='telegramId'
                  render={({ field }) => (
                    <Input {...field} id='telegramId' placeholder='+1 609 933 4422' invalid={errors.telegramId && true} />
                  )}
                />
              </Col>
              <Col md={6} xs={12}>
                <Label className='form-label' for='status'>
                  Status:
                </Label>
                <Controller
                  defaultValue={selectedUser.status}
                  control={control}
                  id='status'
                  name='status'
                  render={({ field: {onChange, value }}) => (
                    <Select 
                      id='status'
                      isClearable={false}
                      className='react-select'
                      classNamePrefix='select'
                      options={statusOptions}
                      value={statusOptions.find((c) => c.value === value)}
                      onChange={val => onChange(val.value)}
                      theme={selectThemeColors}
                      // defaultValue={statusOptions[statusOptions.findIndex(i => i.value === selectedUser.status)]}
                    />
                  )}
                />
              </Col>
              <Col md={6} xs={12}>  
                <Label className='form-label' for='role'>
                  Role:
                </Label>
                <Controller
                  defaultValue={selectedUser.roleId}
                  control={control}
                  id='role'
                  name='role'
                  render={({ field: {onChange, value }}) => (
                    <Select 
                      id='role'
                      isClearable={false}
                      className='react-select'
                      classNamePrefix='select'
                      options={roleOptions}
                      value={roleOptions.find((c) => c.value === value)}
                      onChange={val => onChange(val.value)}
                      theme={selectThemeColors}
                      // defaultValue={statusOptions[statusOptions.findIndex(i => i.value === selectedUser.status)]}
                    />
                  )}
                />
              </Col>
              {/* <Col md={6} xs={12}>
                <Label className='form-label' for='language'>
                  language
                </Label>
                <Select
                  id='language'
                  isClearable={false}
                  className='react-select'
                  classNamePrefix='select'
                  options={languageOptions}
                  theme={selectThemeColors}
                  defaultValue={languageOptions[0]}
                />
              </Col> */}
              {/* <Col md={6} xs={12}>
                <Label className='form-label' for='country'>
                  Country
                </Label>
                <Select
                  id='country'
                  isClearable={false}
                  className='react-select'
                  classNamePrefix='select'
                  options={countryOptions}
                  theme={selectThemeColors}
                  defaultValue={countryOptions[0]}
                />
              </Col> */}
              {/* <Col xs={12}>
                <div className='d-flex align-items-center mt-1'>
                  <div className='form-switch'>
                    <Input type='switch' defaultChecked id='billing-switch' name='billing-switch' />
                    <Label className='form-check-label' htmlFor='billing-switch'>
                      <span className='switch-icon-left'>
                        <Check size={14} />
                      </span>
                      <span className='switch-icon-right'>
                        <X size={14} />
                      </span>
                    </Label>
                  </div>
                  <Label className='form-check-label fw-bolder' for='billing-switch'>
                    Use as a billing address?
                  </Label>
                </div>
              </Col> */}
              <Col xs={12} className='text-center mt-2 pt-50'>
                <Button type='submit' className='me-1' color='primary'>
                  Submit
                </Button>
                <Button
                  type='reset'
                  color='secondary'
                  outline
                  onClick={() => {
                    handleReset()
                    setShow(false)
                  }}
                >
                  Discard
                </Button>
              </Col>
            </Row>
          </Form>
        </ModalBody>
      </Modal>
    </Fragment>
  )
}

export default UserInfoCard
